//// Enum/struct setups

// Scenes
typedef enum {
    FlipperTunerMainMenuScene,
    FlipperTunerPlayToneScene,
    FlipperTunerMetronomeScene,
    FlipperTunerMetronomeSettingsScene,
    FlipperTunerSceneCount,
} FlipperTunerScene;

// Views
typedef enum {
    FlipperTunerSubmenuView,
    FlipperTunerPlayToneView,
    FlipperTunerMetronomeView,
    FlipperTunerVariableItemListView,
} FlipperTunerView;

typedef struct TunerState {
    float volume;
    bool is_playing;
    NOTE current_note;
    int current_note_index;
} TunerState;

typedef struct MetronomeState {
    FuriTimer* timer;
    int bpm;
    bool is_playing;
    NOTE accent_note;
    NOTE normal_note;
    int beats_per_measure;
    int current_beat_count;
    bool accent_first_note;
} MetronomeState;

// App object struct
typedef struct App {
    SceneManager* scene_manager;
    ViewDispatcher* view_dispatcher;
    Submenu* submenu;
    View* play_tone_view;
    TunerState* tuner_state;
    View* metronome_view;
    MetronomeState* metronome_state;
    VariableItemList* variable_item_list;
} App;

typedef enum {
    FlipperTunerMainMenuScenePlayTone,
    FlipperTunerMainMenuSceneMetronome,
} FlipperTunerMainMenuSceneIndex;

typedef enum {
    FlipperTunerMainMenuScenePlayToneEvent,
    FlipperTunerMainMenuSceneMetronomeEvent,
} FlipperTunerMainMenuEvent;

int get_tunings_size() {
    return (sizeof(tunings) / sizeof(tunings[0]));
}

