void flipper_tuner_menu_callback(void* context, uint32_t index) {
    App* app = context;
    switch(index) {
    case FlipperTunerMainMenuScenePlayTone:
        scene_manager_handle_custom_event(
            app->scene_manager, FlipperTunerMainMenuScenePlayToneEvent);
        break;
    case FlipperTunerMainMenuSceneMetronome:
        scene_manager_handle_custom_event(
            app->scene_manager, FlipperTunerMainMenuSceneMetronomeEvent);
        break;
    }
}

// Main Menu scene
void flipper_tuner_main_menu_scene_on_enter(void* context) {
    App* app = context;
    submenu_reset(app->submenu); // Reset submenu
    submenu_set_header(app->submenu, "Flipper Tuner"); // Set header
    submenu_add_item(
        app->submenu,
        "Play Tone",
        FlipperTunerMainMenuScenePlayTone,
        flipper_tuner_menu_callback,
        app);
    submenu_add_item(
        app->submenu,
        "Metronome",
        FlipperTunerMainMenuSceneMetronome,
        flipper_tuner_menu_callback,
        app);
    view_dispatcher_switch_to_view(app->view_dispatcher, FlipperTunerSubmenuView);
}
bool flipper_tuner_main_menu_scene_on_event(void* context, SceneManagerEvent event) {
    App* app = context;
    bool consumed = false;

    switch(event.type) {
    case SceneManagerEventTypeCustom:
        switch(event.event) {
        case FlipperTunerMainMenuScenePlayToneEvent:
            scene_manager_next_scene(app->scene_manager, FlipperTunerPlayToneScene);
            consumed = true;
            break;
        case FlipperTunerMainMenuSceneMetronomeEvent:
            scene_manager_next_scene(app->scene_manager, FlipperTunerMetronomeScene);
            consumed = true;
            break;
        }
        break;
    default:
        break;
    }
    return consumed;
}
void flipper_tuner_main_menu_scene_on_exit(void* context) {
    App* app = context;
    submenu_reset(app->submenu);
}

// Speaker functions
void play_tuner(App* app) {
    TunerState* tuner_state = app->tuner_state;
    furi_hal_speaker_start(tuner_state->current_note.frequency, tuner_state->volume);
    tuner_state->is_playing = true;
}

void play_metronome(App* app) {
    MetronomeState* metronome_state = app->metronome_state;

    if(metronome_state->current_beat_count == 1 && metronome_state->accent_first_note) {
        furi_hal_speaker_start(app->metronome_state->accent_note.frequency, 1);
    } else {
        furi_hal_speaker_start(app->metronome_state->normal_note.frequency, 1);
    }
}

void play(App* app, bool is_tuner) {
    if(furi_hal_speaker_is_mine() || furi_hal_speaker_acquire(1000)) {
        if(is_tuner) {
            play_tuner(app);
        } else {
            play_metronome(app);
        }
    }
}

void stop(App* app, bool is_tuner) {
    if(furi_hal_speaker_is_mine()) {
        furi_hal_speaker_stop();
        furi_hal_speaker_release();
        if(is_tuner) {
            app->tuner_state->is_playing = false;
        }
    }
}

void restart(App* app, bool is_tuner) {
    if(is_tuner) {
        if(app->tuner_state->is_playing) {
            stop(app, true);
            play(app, true);
        }
    }
}

void tuner_increase_volume(TunerState* tuner_state) {
    if(tuner_state->volume < 1.0f) {
        tuner_state->volume += 0.1f;
    }
}

void tuner_decrease_volume(TunerState* tuner_state) {
    if(tuner_state->volume > 0.0f) {
        tuner_state->volume -= 0.1f;
    }
}

void tuner_increase_frequency(TunerState* tuner_state, int semitones) {
    if(tuner_state->current_note_index + semitones < get_tunings_size()) {
        tuner_state->current_note_index += semitones;
        tuner_state->current_note = tunings[tuner_state->current_note_index];
    }
}

void tuner_decrease_frequency(TunerState* tuner_state, int semitones) {
    if(tuner_state->current_note_index - semitones >= 0) {
        tuner_state->current_note_index -= semitones;
        tuner_state->current_note = tunings[tuner_state->current_note_index];
    }
}

// Play Tone Scene & View
static bool play_tone_input_callback(InputEvent* event, void* context) {
    App* app = context;
    TunerState* tuner_state = app->tuner_state;
    bool consumed = false;

    if(event->type == InputTypeShort) {
        // Stop/start
        switch(event->key) {
        case InputKeyOk:
            if(!tuner_state->is_playing) {
                play(app, true);
            } else {
                stop(app, true);
            }
            break;
        case InputKeyUp:
            tuner_increase_volume(tuner_state);
            restart(app, true);
            break;
        case InputKeyDown:
            tuner_decrease_volume(tuner_state);
            restart(app, true);
            break;
        case InputKeyLeft:
            tuner_decrease_frequency(tuner_state, 1);
            restart(app, true);
            break;
        case InputKeyRight:
            tuner_increase_frequency(tuner_state, 1);
            restart(app, true);
            break;
        case InputKeyBack:
            scene_manager_handle_back_event(app->scene_manager);
            break;
        default:
            break;
        }
        consumed = true;
    } else if(event->type == InputTypeLong) {
        switch(event->key) {
        case InputKeyUp:
            tuner_state->volume = 1;
            restart(app, true);
            break;
        case InputKeyDown:
            tuner_state->volume = 0;
            restart(app, true);
            break;
        case InputKeyLeft:
            tuner_decrease_frequency(tuner_state, 12);
            restart(app, true);
            break;
        case InputKeyRight:
            tuner_increase_frequency(tuner_state, 12);
            restart(app, true);
            break;
        case InputKeyBack:
            scene_manager_handle_back_event(app->scene_manager);
            break;
        default:
            break;
        }
        consumed = true;
    }

    with_view_model(
        app->play_tone_view,
        TunerState * model,
        { UNUSED(model); },
        true); // Call to update view
    return consumed;
}

void play_tone_view_draw_callback(Canvas* canvas, void* model) {
    furi_assert(model);
    TunerState* tuner_state = model;

    canvas_draw_frame(canvas, 0, 0, 128, 64);
    canvas_set_font(canvas, FontPrimary);

    FuriString* info = furi_string_alloc();

    // Draw note label
    furi_string_printf(info, "< %s >", tuner_state->current_note.label);
    canvas_draw_str_aligned(canvas, 64, 26, AlignCenter, AlignCenter, furi_string_get_cstr(info));

    // Draw volume
    int volumePercent = (int)round(tuner_state->volume * 100);
    furi_string_printf(info, "Volume: %d%%", volumePercent);
    canvas_draw_str_aligned(canvas, 64, 36, AlignCenter, AlignCenter, furi_string_get_cstr(info));

    furi_string_free(info);

    // Draw controls
    canvas_set_font(canvas, FontSecondary);
    tuner_state->is_playing ? elements_button_center(canvas, "Stop") :
                              elements_button_center(canvas, "Play");
    elements_button_right(canvas, "Note ^");
    elements_button_left(canvas, "Note v");
    elements_button_down(canvas, "Vol v");
    elements_button_up(canvas, "Vol ^");
}

void flipper_tuner_play_tone_scene_on_enter(void* context) {
    App* app = context;
    view_dispatcher_switch_to_view(app->view_dispatcher, FlipperTunerPlayToneView);
}
bool flipper_tuner_play_tone_scene_on_event(void* context, SceneManagerEvent event) {
    UNUSED(context);
    UNUSED(event);
    return false;
}
void flipper_tuner_play_tone_scene_on_exit(void* context) {
    App* app = context;
    if(app->tuner_state->is_playing) {
        stop(app, true);
    }
}

// Metronome Scene & View
void metronome_timer_callback(void* context) {
    App* app = context;

    // Beep
    play(app, false);
    furi_delay_ms(50);
    stop(app, false);

    // Increase beat count
    if(app->metronome_state->current_beat_count == app->metronome_state->beats_per_measure) {
        app->metronome_state->current_beat_count = 1; // Reset
    } else {
        app->metronome_state->current_beat_count += 1;
    }
}

static uint32_t state_to_sleep_ticks(MetronomeState* metronome_state) {
    // Calculate time between beeps
    uint32_t tps = furi_kernel_get_tick_frequency();
    double multiplier = 4.0 / 4;
    double bps = (double)metronome_state->bpm / 60;
    return (uint32_t)(round(tps / bps) - ((50 / 1000) * tps)) * multiplier; // 50: beep length
}

void restart_timer(MetronomeState* metronome_state) {
    if(furi_timer_is_running(metronome_state->timer)) {
        furi_timer_stop(metronome_state->timer);
        furi_timer_start(metronome_state->timer, state_to_sleep_ticks(metronome_state));
    }
}

void increase_bpm(MetronomeState* metronome_state, int amount) {
    if(metronome_state->bpm == 280) return; // BPM ceiling
    metronome_state->bpm += amount;
    restart_timer(metronome_state);
}

void decrease_bpm(MetronomeState* metronome_state, int amount) {
    if(metronome_state->bpm == 60) return; // BPM floor
    metronome_state->bpm -= amount;
    restart_timer(metronome_state);
}

static bool metronome_input_callback(InputEvent* event, void* context) {
    App* app = context;
    MetronomeState* metronome_state = app->metronome_state;

    bool consumed = false;

    if(event->type == InputTypeShort) {
        switch(event->key) {
        case InputKeyOk:
            metronome_state->is_playing = !metronome_state->is_playing;
            metronome_state->is_playing ?
                furi_timer_start(metronome_state->timer, state_to_sleep_ticks(metronome_state)) :
                furi_timer_stop(metronome_state->timer);
            break;
        case InputKeyUp:
            increase_bpm(metronome_state, 1);
            break;
        case InputKeyDown:
            decrease_bpm(metronome_state, 1);
            break;
        case InputKeyRight:
            scene_manager_next_scene(app->scene_manager, FlipperTunerMetronomeSettingsScene);
            break;
        case InputKeyBack:
            scene_manager_handle_back_event(app->scene_manager);
            break;
        default:
            break;
        }
        consumed = true;
    } else if(event->type == InputTypeLong) {
        switch(event->key) {
        case InputKeyUp:
            increase_bpm(metronome_state, 10);
            break;
        case InputKeyDown:
            decrease_bpm(metronome_state, 10);
            break;
        default:
            break;
        }
        consumed = true;
    }
    with_view_model(
        app->metronome_view,
        MetronomeState * model,
        { UNUSED(model); },
        true); // Call to update view

    return consumed;
}

void metronome_view_draw_callback(Canvas* canvas, void* model) {
    furi_assert(model);
    MetronomeState* metronome_state = model;

    canvas_draw_frame(canvas, 0, 0, 128, 64);
    canvas_set_font(canvas, FontPrimary);

    FuriString* info = furi_string_alloc();

    // Draw BPM
    furi_string_printf(info, "BPM: %d", metronome_state->bpm);
    canvas_draw_str_aligned(canvas, 64, 21, AlignCenter, AlignCenter, furi_string_get_cstr(info));

    // Draw time signature
    furi_string_printf(info, "%d/4", metronome_state->beats_per_measure);
    canvas_draw_str_aligned(canvas, 64, 33, AlignCenter, AlignCenter, furi_string_get_cstr(info));

    furi_string_free(info);

    // Draw controls
    canvas_set_font(canvas, FontSecondary);
    metronome_state->is_playing ? elements_button_center(canvas, "Stop") :
                                  elements_button_center(canvas, "Play");
    elements_button_up(canvas, "BPM ^");
    elements_button_down(canvas, "BPM v");
    elements_progress_bar(
        canvas,
        28,
        41,
        72,
        (double)metronome_state->current_beat_count / metronome_state->beats_per_measure);
}

void flipper_tuner_metronome_scene_on_enter(void* context) {
    App* app = context;
    app->metronome_state->current_beat_count = 1; // Reset to beginning of measure
    view_dispatcher_switch_to_view(app->view_dispatcher, FlipperTunerMetronomeView);
}
bool flipper_tuner_metronome_scene_on_event(void* context, SceneManagerEvent event) {
    UNUSED(context);
    UNUSED(event);
    return false;
}
void flipper_tuner_metronome_scene_on_exit(void* context) {
    App* app = context;
    if(furi_timer_is_running(app->metronome_state->timer)) {
        furi_timer_stop(app->metronome_state->timer);
        app->metronome_state->is_playing = false;
    }
}

// Metronome Settings Scene & View
void beats_per_measure_change_callback(VariableItem* item) {
    App* app = variable_item_get_context(item);

    // Get bpm info
    uint8_t index = variable_item_get_current_value_index(item);
    FuriString* info = furi_string_alloc();
    furi_string_printf(info, "%d", beats_per_measure_options[index]);

    // Set text & variable
    variable_item_set_current_value_text(item, furi_string_get_cstr(info));
    app->metronome_state->beats_per_measure = beats_per_measure_options[index];
    app->metronome_state->current_beat_count = 1; // Reset to beginning of measure
}

void accent_first_note_change_callback(VariableItem* item) {
    App* app = variable_item_get_context(item);

    if(variable_item_get_current_value_index(item) == 0) {
        variable_item_set_current_value_text(item, "Yes");
        app->metronome_state->accent_first_note = true;
    } else {
        variable_item_set_current_value_text(item, "No");
        app->metronome_state->accent_first_note = false;
    }
}

void flipper_tuner_metronome_settings_scene_on_enter(void* context) {
    App* app = context;
    view_dispatcher_switch_to_view(app->view_dispatcher, FlipperTunerVariableItemListView);
}
bool flipper_tuner_metronome_settings_scene_on_event(void* context, SceneManagerEvent event) {
    UNUSED(context);
    UNUSED(event);
    return false;
}
void flipper_tuner_metronome_settings_scene_on_exit(void* context) {
    UNUSED(context);
}

