// Memory allocation
static void play_tone_alloc(void* context) {
    App* app = context;

    app->play_tone_view = view_alloc();

    view_set_draw_callback(app->play_tone_view, play_tone_view_draw_callback);
    view_allocate_model(app->play_tone_view, ViewModelTypeLockFree, sizeof(TunerState));
    app->tuner_state = view_get_model(app->play_tone_view);

    app->tuner_state->current_note_index = 57; // A4
    app->tuner_state->current_note = tunings[app->tuner_state->current_note_index];
    app->tuner_state->volume = 1.0f;
    app->tuner_state->is_playing = false;

    view_set_input_callback(app->play_tone_view, play_tone_input_callback);
    view_set_context(app->play_tone_view, app);

    view_dispatcher_add_view(app->view_dispatcher, FlipperTunerPlayToneView, app->play_tone_view);
}

static void metronome_settings_alloc(void* context) {
    App* app = context;

    app->variable_item_list = variable_item_list_alloc();

    // Define beats per measure
    app->metronome_state->beats_per_measure = 4;

    VariableItem* bpm = variable_item_list_add(
        app->variable_item_list,
        "Beats/Measure",
        COUNT_OF(beats_per_measure_options),
        beats_per_measure_change_callback,
        app);
    // Set values for beats per measure
    variable_item_set_current_value_index(bpm, app->metronome_state->beats_per_measure - 1);
    FuriString* info = furi_string_alloc();
    furi_string_printf(
        info, "%d", beats_per_measure_options[variable_item_get_current_value_index(bpm)]);
    variable_item_set_current_value_text(bpm, furi_string_get_cstr(info));
    furi_string_free(info);

    // Define accent first note
    app->metronome_state->accent_first_note = true;

    VariableItem* accent = variable_item_list_add(
        app->variable_item_list, "Accent First Note", 2, accent_first_note_change_callback, app);
    variable_item_set_current_value_index(accent, 0); // Initialize to "Yes"
    variable_item_set_current_value_text(accent, "Yes");

    view_dispatcher_add_view(
        app->view_dispatcher,
        FlipperTunerVariableItemListView,
        variable_item_list_get_view(app->variable_item_list));
}

static void metronome_alloc(void* context) {
    App* app = context;

    app->metronome_view = view_alloc();

    view_set_draw_callback(app->metronome_view, metronome_view_draw_callback);
    view_allocate_model(app->metronome_view, ViewModelTypeLockFree, sizeof(MetronomeState));
    app->metronome_state = view_get_model(app->metronome_view);

    app->metronome_state->bpm = 120;
    app->metronome_state->accent_note = tunings[69]; // A5
    app->metronome_state->normal_note = tunings[57]; // A4
    app->metronome_state->is_playing = false;
    app->metronome_state->current_beat_count = 1;

    app->metronome_state->timer =
        furi_timer_alloc(metronome_timer_callback, FuriTimerTypePeriodic, app);

    view_set_input_callback(app->metronome_view, metronome_input_callback);
    view_set_context(app->metronome_view, app);

    view_dispatcher_add_view(app->view_dispatcher, FlipperTunerMetronomeView, app->metronome_view);

    metronome_settings_alloc(app);
}

static App* app_alloc() {
    App* app = malloc(sizeof(App));
    app->scene_manager = scene_manager_alloc(&flipper_tuner_scene_manager_handlers, app);
    app->view_dispatcher = view_dispatcher_alloc();

    view_dispatcher_set_event_callback_context(app->view_dispatcher, app);
    view_dispatcher_set_custom_event_callback(app->view_dispatcher, basic_scene_custom_callback);
    view_dispatcher_set_navigation_event_callback(
        app->view_dispatcher, basic_scene_back_event_callback);

    // Submenu view
    app->submenu = submenu_alloc();
    view_dispatcher_add_view(
        app->view_dispatcher, FlipperTunerSubmenuView, submenu_get_view(app->submenu));

    play_tone_alloc(app);

    metronome_alloc(app);

    return app;
}

static void app_free(App* app) {
    furi_assert(app);

    // Remove views
    view_dispatcher_remove_view(app->view_dispatcher, FlipperTunerSubmenuView);
    view_dispatcher_remove_view(app->view_dispatcher, FlipperTunerPlayToneView);
    view_dispatcher_remove_view(app->view_dispatcher, FlipperTunerMetronomeView);
    view_dispatcher_remove_view(app->view_dispatcher, FlipperTunerVariableItemListView);

    // Memory free
    scene_manager_free(app->scene_manager);
    view_dispatcher_free(app->view_dispatcher);
    submenu_free(app->submenu);
    variable_item_list_free(app->variable_item_list);

    view_free(app->play_tone_view);
    free(app->tuner_state);

    view_free(app->metronome_view);
    //furi_timer_free(app->metronome_state->timer);
    free(app->metronome_state);
    free(app);
}

int32_t flipper_tuner_app(void* p) {
    UNUSED(p);
    App* app = app_alloc();

    Gui* gui = furi_record_open(RECORD_GUI);
    view_dispatcher_attach_to_gui(
        app->view_dispatcher, gui, ViewDispatcherTypeFullscreen); // Initialize GUI
    scene_manager_next_scene(app->scene_manager, FlipperTunerMainMenuScene); // Begin at main menu
    view_dispatcher_run(app->view_dispatcher);

    app_free(app);
    return 0;
}
